/*eslint-disable */
//노랑 워닝 안뜨게 하는 것

import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
let [modal, modalChange] = useState(0);{/*스위치*/}
  let [글제목, 글제목변경] = useState(["HTML이란?", 'CSS란?','JS란?','REACT란?']); //state는 변경되어도 새로고침되지 않음. 재렌더링됨.
  let [따봉, 따봉변경] = useState(0);


  function 제목변경(){
    var newArray = [...글제목];
    newArray[0] = "Python이란?";
    글제목변경(newArray);
  }

  return (
    <div className="App">
       <div className="black-nav">
         <div> 개발 Blog</div>
       </div>
       <button onClick={ 제목변경 }>버튼</button>
       <div className="list">
        <h3>{ 글제목[0] } <span onClick={()=>{ 따봉변경(따봉+1) }}>👍</span> {따봉} </h3>
        <p>9월 1일 발행</p>
        <hr/>
       </div>
       <div className="list">
        <h3>{ 글제목[1] }</h3>
        <p>9월 8일 발행</p>
        <hr/>
       </div>
       <div className="list">
        <h3>{ 글제목[2] }</h3>
        <p>9월 15일 발행</p>
        <hr/>
       </div>
       <div className="list">
        <h3>{ 글제목[3] }</h3>
        <p>9월 22일 발행</p>
        <hr/>
       </div>
       <button onClick={()=>{modalChange(modal+1)}}>버튼</button>
       {
         modal%2===1 ? <Modal></Modal> : null
       }
       {/* 삼항연산자: {조건식 ? 참일 경우 실행되는 코드 : 거짓일 경우 실행되는 코드} */}

       {
        글제목.map(function(a){
          return (
          <div className="list">
          <h3>{ a }<span onClick={()=>{ 따봉변경(따봉+1) }}>👍</span> {따봉}</h3>
          <p>9월 22일 발행</p>
          <hr/>
         </div>
          )
        })
       }
    </div>
  );
}
{/* component: html 줄이는 방법 
유의사항: 1. 이름은 대괄호
        2. return()안에 있는 건 태그 하나로 묶여있어야 함.(fragment: <> </>로 div 대체 가능)
        3. functiona app도 하나의 컴포먼트, 새로 만드는 컴포먼트들은 app과 같은 위치에 놓기( 수평적인 관계 ).
        4. 컴포먼트로 만들면 좋은 것: list처럼 반복되는 div class들. 자주 바뀌는 UI

        5. 단, 너무 많이 만들면 state 쓸 때 복잡해짐. function 밖으로 나가게 되기 때문에 다시 가져다가 쓸 수 있음. ex. Modal에 글제목[0]이런 식으로 써줄 수 있음. props라는 문법을 이해해서 써야함.
*/}
function Modal(){
  return(
    <div className="modal">
      <h2>제목</h2>
      <p>날짜</p>
      <p>상세 내용</p>
    </div>
  )
}

export default App;
